" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathogenesis of sarcoidosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/14/6376/contributors\">",
