var title_f6_16_6412="Classification of pityriasis rubra pilaris";
var content_f6_16_6412=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F87505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F87505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of pityriasis rubra pilaris",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        PRP type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical characteristics",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I (classic adult)*",
"       </td>",
"       <td>",
"        Wide distribution of follicular papules and plaques with cephalocaudal progression and sparing islands of normal-appearing skin",
"       </td>",
"       <td>",
"        Favorable - 80 percent of patients resolve spontaneously within three years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        II (atypical adult)",
"       </td>",
"       <td>",
"        Ichthyosiform dermatitis, palmoplantar hyperkeratosis, chronic course",
"       </td>",
"       <td>",
"        Chronic course",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        III (classic juvenile)",
"       </td>",
"       <td>",
"        Wide distribution of follicular papules and plaques with cephalocaudal progression and spared islands of normal-appearing skin",
"       </td>",
"       <td>",
"        Often remission after one year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV (circumscribed juvenile)",
"       </td>",
"       <td>",
"        Circumscribed lesions restricted to the elbows and knees and frequent palmoplantar hyperkeratosis",
"       </td>",
"       <td>",
"        Unclear",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        V (atypical juvenile)",
"       </td>",
"       <td>",
"        Ichthyosiform dermatitis, palmoplantar hyperkeratosis",
"       </td>",
"       <td>",
"        Chronic course",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        VI (HIV-associated)",
"       </td>",
"       <td>",
"        Similar to type I",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Most common form of PRP.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Griffiths WA, Ozluer S. [Pityriasis rubra pilaris]. Ann Dermatol Venereol 2001; 128:931.",
"      </li>",
"      <li>",
"       Griffiths WA. Pityriasis rubra pilaris. Clin Exp Dermatol 1980; 5:105.",
"      </li>",
"      <li>",
"       Klein A, Landthaler M, Karrer S. Pityriasis rubra pilaris: a review of diagnosis and treatment. Am J Clin Dermatol 2010; 11:157.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
