var title_f6_16_6413="Energy preservation methods";
var content_f6_16_6413=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F53415&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F53415&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ways to preserve energy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Basic activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Bathing and grooming",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wash your hair in the shower rather than over a sink.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use a terry robe instead of towels to dry off.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use organizers to keep things within reach.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use a chair in the shower or tub.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use a long-handled sponge or brush to reach your back and feet.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rest elbows on counter or dressing table to avoid leaning unsupported.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use long handled brushes or combs to avoid holding your arms overhead.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use liquid soap or soap on a rope.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use an elevated toilet seat.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Dressing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wear loose clothes that are easier to put on and take off.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bring your foot to your knee to put on socks and shoes so you won't have to lean over.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wear slip-on shoes or shoes with Velcro closures or elastic shoelaces.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use a long handled shoe horn or sock aid.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fasten your bra in front and then turn it around.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wear clothes that button in front rather than pullovers or clothes that button in the back.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use a reacher or dressing stick to help with zippers in back.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Advanced activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Housework",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spread tasks out over the week or month.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hire help.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use long-handled dusters, mops, dust pans.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use automatic washer and dryer if possible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use lightweight iron.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Shopping",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Make a list.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Organize your shopping list by store aisle.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Request assistance in stores.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Have groceries delivered if possible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Use canned, frozen, or other prepared foods, or try ordering out.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        On days when you feel well, cook extra food and freeze in small containers. Reheat frozen food on days when you're tired.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ask your doctor, nurse, or social worker about community resources that deliver prepared meals.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Eat small, frequent meals rather than several large meals each day.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Have snack foods readily available.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        If you're really exhausted, put some favorite foods and beverages in a cooler with ice next to you as you rest.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Make quick meals with a microwave or blender.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
