var title_f6_17_6428="Methods of summarizing studies in systematic reviews";
var content_f6_17_6428=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F80657&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F80657&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Methods of summarizing studies in systematic reviews",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Approach",
"       </td>",
"       <td class=\"subtitle1\">",
"        Main model",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Qualitative systematic review",
"       </td>",
"       <td>",
"        Qualitative description of evidence",
"       </td>",
"       <td>",
"        May be most appropriate if large degree of heterogeneity among studies.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Meta-analysis overall estimate",
"       </td>",
"       <td>",
"        Fixed effects model",
"       </td>",
"       <td>",
"        Assumes a single truth across populations and homogeneity among studies.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Random effects model",
"       </td>",
"       <td>",
"        Incorporates between-study heterogeneity. Wider confidence interval than fixed effects model when heterogeneity is present.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meta-analysis of multiple interventions simultaneously",
"       </td>",
"       <td>",
"        Network meta-analysis (mixed or multiple treatment comparison)",
"       </td>",
"       <td>",
"        Estimates relative effects of multiple interventions against each other, even if no individual study analyzed a comparison between 2 or more specific interventions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meta-analysis subgroup analyses",
"       </td>",
"       <td>",
"        Fixed or random effects models",
"       </td>",
"       <td>",
"        May explain heterogeneity. Subject to ecological fallacy. May not be possible due to limited reported data. Arbitrary selection of subgroups may result in spurious findings.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meta-regression",
"       </td>",
"       <td>",
"        Regression across studies",
"       </td>",
"       <td>",
"        May explain heterogeneity. Subject to ecological fallacy. May not be possible due to limited reported data.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individual patient data meta-analysis",
"       </td>",
"       <td>",
"        Multivariate regression across individuals",
"       </td>",
"       <td>",
"        Allows most complete analysis of data and evaluation of heterogeneity. Costly and resource-intensive.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
