var title_f6_17_6429="Oral antibiotics human bites";
var content_f6_17_6429=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F69328&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F69328&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric oral antibiotic therapy for human bites",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Antibiotic",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dosing for adults",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dosing for children",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        Agent of choice:",
"       </p>",
"       <p>",
"        Amoxicillin-clavulanate",
"       </p>",
"      </td>",
"      <td>",
"       875/125 mg twice daily",
"      </td>",
"      <td>",
"       20 mg/kg per dose (amoxicillin component) two times daily (maximum 875 mg amoxicillin and 125 mg clavulinic acid per dose)*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Alternate empiric regimens include:&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\" colspan=\"3\">",
"       One of the following agents with activity against",
"       <em>",
"        Eikenella",
"       </em>",
"       :",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Doxycycline &Delta;",
"      </td>",
"      <td class=\"sublist_other\">",
"       100 mg twice daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Not recommended in children &lt;8 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Trimethoprim-sulfamethoxazole &Delta;",
"      </td>",
"      <td class=\"sublist_other\">",
"       1 double strength tablet twice daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       4-5 mg/kg (trimethoprim component) per dose twice daily (maximum 160 mg trimethoprim per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Penicillin VK",
"      </td>",
"      <td class=\"sublist_other\">",
"       500 mg four times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       12.5 mg/kg per dose four times daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Cefuroxime",
"      </td>",
"      <td class=\"sublist_other\">",
"       500 mg twice daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       10 mg/kg per dose twice daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\" colspan=\"3\">",
"       Fluoroquinolones (one of the following):",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist3\">",
"       Ciprofloxacin",
"      </td>",
"      <td class=\"sublist_other\">",
"       500 to 750 mg twice daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Use with caution in children &lt;18 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist3\">",
"       Moxifloxacin",
"      </td>",
"      <td class=\"sublist_other\">",
"       400 mg once daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       Use with caution in children &lt;18 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\" colspan=\"3\">",
"       <strong>",
"        PLUS",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\" colspan=\"3\">",
"       One of the following agents with anaerobic activity:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Metronidazole",
"      </td>",
"      <td class=\"sublist_other\">",
"       500 mg three times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       10 mg/kg per dose three times daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Clindamycin &Delta;",
"      </td>",
"      <td class=\"sublist_other\">",
"       450 mg three times daily",
"      </td>",
"      <td class=\"sublist_other\">",
"       10 mg/kg per dose three times daily (maximum 450 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       The following agents have poor activity against",
"       <em>",
"        Eikenella",
"       </em>",
"       and should be avoided:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Cephalexin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Dicloxacillin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Erythromycin",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In patients with infected bite wounds, the clinician may increase the does to 45 mg/kg per dose (amoxicillin component) twice daily (maximum 875 mg amoxicillin and 125 mg clavulinic acid per dose).",
"     <br>",
"      &bull; The preferred regimen for pencillin allergic pediatric patients is trimethoprim-sulfamethoxazole OR cefuroxime PLUS clindamycin (depending on liquid drug availability and palatability).",
"      <br>",
"       &Delta; May also be active against MRSA (check susceptibility testing).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
