" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Common bile duct exploration",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/18/6440/contributors\">",
