var title_f6_18_6444="Spleen injury scale";
var content_f6_18_6444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F67884&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F67884&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Spleen injury scale (1994 revision)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Injury type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Injury description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        I",
"       </td>",
"       <td>",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, &lt;10 percent surface area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Capsular tear, &lt;1 cm parenchymal depth",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        II",
"       </td>",
"       <td>",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, 10-50 percent surface area; intraparenchymal, &lt;5 cm in diameter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        1-3 cm parenchymal depth which does not involve a trabecular vessel",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        III",
"       </td>",
"       <td rowspan=\"2\">",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, &gt;50 percent surface area or expanding; ruptured subcapsular or parenchymal hematoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intraparenchymal hematoma &gt;5 cm or expanding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        &gt;3 cm parenchymal depth or involving trabecular vessels",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Laceration involving segmental or hilar vessels producing major devascularization (&gt;25 percent of spleen)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        V",
"       </td>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Completely shattered spleen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vascular",
"       </td>",
"       <td>",
"        Hilar vascular injury which devascularizes spleen",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Advance one grade for multiple injuries, up to grade III.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Moore, EE, Cogbill, TH, Jurkovich, GJ, et al. Organ injury scaling: spleen and liver (1994 revision). J Trauma 1995; 38:323.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
