var title_f6_2_6188="Crying framework";
var content_f6_2_6188=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F77427&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F77427&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Lester's framework for colic and excessive crying",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Colic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Meets the \"rule of three\" criterion:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Greater than 3 hours per day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Greater than 3 days per week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lasts at least 3 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Includes the additional four characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Paroxysmal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Qualitatively different from normal crying",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Associated with hypertonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Inconsolability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The infant is normal when not colicky",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        First few weeks of life are unremarkable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Excessive crying",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meets the definition of colic except does not have the additional four characteristics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Early onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colic and/or excessive crying starts at or shortly after birth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Persistent crying",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infant fusses or cries virtually all the time that he or she is awake (except perhaps during feeding)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infant is described as never being happy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May accompany colic or excessive crying",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Lester, BM. In: Colic and Excessive Crying. Report of the 105th Ross Conference on Pediatric Research, Lester, BM, Barr, RG (Eds), Ross Products Division,Columbus, OH, 1997, p.18.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
