var title_f6_2_6189="Comparison of bronchiolitis I";
var content_f6_2_6189=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50736%7EPULM%2F60031&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50736%7EPULM%2F60031&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Proliferative versus constrictive bronchiolitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Proliferative bronchiolitis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Constrictive bronchiolitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histopathologic manifestations",
"       </td>",
"       <td>",
"        <p>",
"         Common finding",
"        </p>",
"        <p>",
"         Nonspecific reparative reaction to bronchiolar injury",
"        </p>",
"        <p>",
"         Organizing intraluminal exudate",
"        </p>",
"        <p>",
"         Most prominent in alveolar ducts",
"        </p>",
"        <p>",
"         Inflammatory changes in surrounding alveolar walls",
"        </p>",
"        <p>",
"         Foamy macrophages in alveoli",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Very uncommon finding",
"        </p>",
"        <p>",
"         Obliterans not a constant feature",
"        </p>",
"        <p>",
"         Variety",
"of histologic changes: bronchiolar inflammation to progressive",
"concentric fibrosis; smooth muscle hyperplasia, bronchioloectasia with",
"mucus stasis; distortion and fibrosis of small airway walls with",
"bronchial metaplasia extending onto peribronchiolar alveolar septa",
"        </p>",
"        <p>",
"         Follicular bronchitis (lymphoid hyperplasia)",
"        </p>",
"        <p>",
"         Cellular bronchiolitis",
"        </p>",
"        <p>",
"         Diffuse panbronchiolitis",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radiographic abnormalities",
"       </td>",
"       <td>",
"        <p>",
"         Bilateral patchy airspace opacities",
"        </p>",
"        <p>",
"         Interstitial opacities",
"        </p>",
"        <p>",
"         Small rounded opacities",
"        </p>",
"        <p>",
"         Opacities may be recurrent and migratory",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         May be normal",
"        </p>",
"        <p>",
"         Progressive increase in lung volume on serial radiographs",
"        </p>",
"        <p>",
"         HRCT scan may show marked heterogeneity of lung density",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary function",
"       </td>",
"       <td>",
"        Restrictive defect (a mixed pattern may be seen)",
"       </td>",
"       <td>",
"        Obstructive defect with hyperinflation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from King, TE Jr, Clin Chest Med 1993; 14:607.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Proliferative versus constrictive bronchiolitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       &nbsp;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Proliferative bronchiolitis",
"      </td>",
"      <td class=\"subtitle1\">",
"       Constrictive bronchiolitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Selected clinical syndromes",
"      </td>",
"      <td>",
"       <p>",
"        Cryptogenic organizing pneumonia (idiopathic bronchiolitis obliterans with organizing pneumonia)",
"       </p>",
"       <p>",
"        Collagen vascular disease (eg, dermatomyositis, SLE)",
"       </p>",
"       <p>",
"        Chronic \"organizing\" pneumonia (especially influenza, Mycoplasma, Pneumocystis carinii)",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Allograft recipients (bone marrow, heart-lung, lung)",
"       </p>",
"       <p>",
"        Collagen vascular disease (rheumatoid arthritis)",
"       </p>",
"       <p>",
"        Post-infectious (especially respiratory syncytial virus, adenovirus, influenza, parainfluenza, Mycoplasma)",
"       </p>",
"       <p>",
"        Inhaled toxins (eg, nitrogen dioxide, sulfur dioxide, ammonia, chlorine, phosgene)",
"       </p>",
"       <p>",
"        Drugs (eg, penicillamine, lomustine)",
"       </p>",
"       <p>",
"        Cigarette smoke",
"       </p>",
"       <p>",
"        Mineral dust airway disease",
"       </p>",
"       <p>",
"        Idiopathic",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Natural history",
"      </td>",
"      <td>",
"       Glucocorticoid responsive and usually reversible",
"      </td>",
"      <td>",
"       Relatively glucocorticoid unresponsive and usually progressive, with the development of irreversible airflow obstruction and air trapping",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from King, TE Jr, Clin Chest Med 1993; 14:607.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
