var title_f6_20_6476="Differential diagnosis acute knee pain athlete B";
var content_f6_20_6476=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F58127&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F58127&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of acute knee pain in the young athlete: part B",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of injury",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical Features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Lateral",
"pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"5\">",
"        LCL injury",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Varus force",
"       </td>",
"       <td>",
"        Localized swelling and stiffness",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Associated with superficial peroneal nerve injuries,",
"chondral injuries, and medial meniscal injuries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Able to ambulate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Localized pain, swelling, ecchymosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain along course of ligament (examine in figure of 4",
"position)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laxity with varus stress and 30 degrees of knee flexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"8\">",
"        Lateral",
"meniscal tear",
"       </td>",
"       <td colspan=\"1\" rowspan=\"8\">",
"        Twisting injury",
"       </td>",
"       <td>",
"        Usually able to ambulate, but weight bearing is limited",
"by pain",
"       </td>",
"       <td colspan=\"1\" rowspan=\"8\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knee swelling and stiffness develop over two to three days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mechanical symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lateral pain with twisting or squatting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lateral joint line",
"tenderness&nbsp;&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive McMurray test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive bounce home test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased range of motion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LCL: Lateral collateral ligament.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
