" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Chelating agents used in the treatment of childhood lead poisoning",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Product name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Generic name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chemical name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Abbreviation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium disodium versenate",
"       </td>",
"       <td>",
"        Edetate disodium calcium",
"       </td>",
"       <td>",
"        Calcium disodium ethylenediamine-tetracetate",
"       </td>",
"       <td>",
"        CaNa",
"        <sub>",
"         2",
"        </sub>",
"        EDTA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BAL* in oil",
"       </td>",
"       <td>",
"        Dimercaprol",
"       </td>",
"       <td>",
"        2,3-dimercapto-propanol",
"       </td>",
"       <td>",
"        BAL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cuprimine",
"       </td>",
"       <td>",
"        D-penicillamine",
"       </td>",
"       <td>",
"        3-merecapto-d-valine",
"       </td>",
"       <td>",
"        D-penicillamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chemet",
"       </td>",
"       <td>",
"        Succimer",
"       </td>",
"       <td>",
"        Meso -2,3-dimercapto- succinic acid",
"       </td>",
"       <td>",
"        DMSA",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * British anti-Lewisite.",
"    </div>",
