var title_f6_3_6203="Rules for good sleep";
var content_f6_3_6203=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71116&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71116&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ten basic rules for a good night's sleep",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Sleep only as much as you need to feel rested and then get out of bed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Keep a regular sleep schedule",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid forcing sleep",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Exercise regularly for at least 20 minutes, preferably 4 to 5 hours before bedtime",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid caffeinated beverages after lunch",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid alcohol near bedtime: no \"night cap\"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid smoking, especially in the evening",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Do not go to bed hungry",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Adjust bedroom environment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Deal with your worries before bedtime",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
