var title_f6_3_6204="Hypothalamic hormones";
var content_f6_3_6204=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F53814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F53814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major hypothalamic hormones and their effect on anterior pituitary hormones",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Hypothalamic stimlatory hormones",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pituitary hormones",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Corticotropin-releasing hormone - 41 amino acids; released from paraventricular neurons as well as supraoptic and arcuate nuclei and limbic system",
"       </td>",
"       <td>",
"        Adrenocorticotropic hormone - basophilic corticotrophs represent 20 percent of cells in anterior pituitary; ACTH is product of proopiomelanocortin (POMC) gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Melanocyte-stimulating hormone - alternate product of POMC gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Endorphins - also products of POMC gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Growth hormone-releasing hormone - two forms, 40 and 44 amino acids",
"       </td>",
"       <td>",
"        Growth hormone - acidophilic somatotrophs represent 50 percent of cells in anterior pituitary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gonadotropin-releasing hormone - 10 amino acids; mostly released from preoptic neurons",
"       </td>",
"       <td>",
"        Luteinizing hormone and follicle-stimulating hormone - gonadotrophs represent about 15 percent of anterior pituitary cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyrotropin-releasing hormone - three amino acids; released from anterior hypothalamic area",
"       </td>",
"       <td>",
"        Thyroid-stimulating hormone - thyrotropes represent about&nbsp;5 percent of anterior pituitary cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolactin-releasing factors - include serotonin, acetylcholine, opiates, and estrogens",
"       </td>",
"       <td>",
"        Prolactin - lactotrophs represent 10 to 30 percent of anterior pituitary cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Hypothalamic inhibitory hormones",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Somatostatin - 14 amino acids",
"       </td>",
"       <td>",
"        Inhibits the release of growth hormone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolactin-inhibiting factors - includes dopamine",
"       </td>",
"       <td>",
"        Major prolactin control is inhibitory",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
