" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Neutrophil functions other than movement",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/31/6650/contributors\">",
