var title_f6_33_6684="Causes of hypercholesterolemia";
var content_f6_33_6684=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F66747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F66747&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Primary disorders of cholesterol metabolism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lipid levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmatory studies",
"       </td>",
"       <td class=\"subtitle1\">",
"        Physical findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial hyper- cholesterolemia (definite)*",
"       </td>",
"       <td>",
"        High TC; TG usually normal",
"       </td>",
"       <td>",
"        LDL receptor defect by cell studies or DNA mapping",
"       </td>",
"       <td>",
"        Tendon xanthomata in patient or one first or second",
"degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial hyper- cholesterolemia (probable)*",
"       </td>",
"       <td>",
"        High TC; TG usually normal",
"       </td>",
"       <td>",
"        Exclusions include secondary disorders of cholesterol",
"metabolism; tendon xanthomata in patient or relative",
"       </td>",
"       <td>",
"        Corneal arcus; xanthelasma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial combined hyperlipidemia",
"       </td>",
"       <td>",
"        TC and/or TG &ge;90th",
"percentile in patient and in one first or two second-degree relatives;",
"apo B &ge;90th",
"percentile",
"       </td>",
"       <td>",
"        LDL-chol/apo B &lt;1.2",
"       </td>",
"       <td>",
"        Xanthelasma; corneal arcus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial hyperapobeta- lipoproteinemia",
"       </td>",
"       <td>",
"        Apo B &gt;90th percentile; at least two first- or",
"second-degree relatives with similar profile",
"       </td>",
"       <td>",
"        LDL-chol/apo B &lt;1.2",
"       </td>",
"       <td>",
"        Xanthelasma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polygenic hyper- cholesterolemia",
"       </td>",
"       <td>",
"        TC &gt;90th percentile; TG &lt;90th percentile",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Exclusions: tendon xanthomata in patient or family",
"members",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Summary of diagnostic criteria for the different forms of hypercholesterolemia.",
"    <div class=\"footnotes\">",
"     TC: total cholesterol; TG: total triglycerides; apo B: apolipoprotein B.",
"     <br>",
"      * The laboratory and clinical manifestations of familial defective apolipoprotein B-100 are identical to those of familial hypercholesterolemia; the distinction can be made only by molecular biology techniques.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Summary of diagnostic criteria for the different forms of hypercholesterolemia.&nbsp;Data from Rosenson RS, Frauenheim WA, Tangney CC. Dis Mon 1994; 40:369.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
