var title_f6_34_6701="Blistering disorders with mucous membrane involvement";
var content_f6_34_6701=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F67630&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F67630&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Blistering disorders with mucous membrane involvement",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated conditions",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pathology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Laboratory findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Herpes simplex virus (HSV)",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Recurrent, grouped vesicles; most commonly found on lips or genitalia",
"       </td>",
"       <td>",
"        H&amp;E: intraepidermal blister; ballooning of keratinocyte cytoplasm and margination of chromatin to form intranuclear inclusion bodies; mixed inflammatory infiltrate",
"       </td>",
"       <td>",
"        Viral culture, Tzanck smear, HSV DFA, PCR",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Erythema multiforme",
"        </strong>",
"       </td>",
"       <td>",
"        Herpes simplex virus or",
"        <em>",
"         Mycoplasma pneumoniae",
"        </em>",
"        infection, occasionally drug exposure",
"       </td>",
"       <td>",
"        Mucosal erythema and erosions, most frequently involving oral cavity; target lesions on skin",
"       </td>",
"       <td>",
"        H&amp;E: vacuolar interface dermatitis, necrotic keratinocytes, subepidermal clefts and vesiculation",
"       </td>",
"       <td>",
"        HSV testing on active lesions, serology, or biopsy specimen; testing for",
"        <em>",
"         M. pneumoniae",
"        </em>",
"        infection",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Pemphigus vulgaris",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Vesicles, erosions and desquamative gingivitis",
"       </td>",
"       <td>",
"        <p>",
"         H&amp;E: intraepidermal vesicle, acantholysis",
"        </p>",
"        <p>",
"         DIF: intercellular epidermal IgG deposition",
"        </p>",
"       </td>",
"       <td>",
"        IIF: intercellular IgG staining of skin substrate, IgG anti-desmoglein 3",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Mucous membrane pemphigoid",
"        </strong>",
"        *",
"       </td>",
"       <td>",
"        Reflects a diverse group of subepidermal blistering disorders with mucous membranes as the primary site of involvement",
"       </td>",
"       <td>",
"        Vesicles and erosions, desquamative gingivitis; can have ocular involvement and symblepharon",
"       </td>",
"       <td>",
"        H&amp;E: subepidermal blisters; inflammatory infiltrate and DIF findings vary with subtype",
"       </td>",
"       <td>",
"        IIF: varies with subtype",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Stevens-Johnson syndrome and toxic epidermal necrolysis",
"        </strong>",
"       </td>",
"       <td>",
"        Medication, M",
"        <em>",
"         ycoplasma",
"        </em>",
"        infection",
"       </td>",
"       <td>",
"        Exudative erosions of lips, oral mucosa, eyes, genital mucosa; targetoid papulovesicles of skin, skin sloughing, skin pain",
"       </td>",
"       <td>",
"        H&amp;E: vacuolar interface dermatitis or epidermal necrosis",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Paraneoplastic pemphigus",
"        </strong>",
"       </td>",
"       <td>",
"        Underlying malignancy",
"       </td>",
"       <td>",
"        Targetoid papulovesicles; erythema multiforme-like cutaneous lesions; exudative erosions of lips, oral mucosal, eyes, genital mucosa",
"       </td>",
"       <td>",
"        <p>",
"         H&amp;E: mixed histology; overlapping features of pemphigus vulgaris, erythema multiforme, and lichen planus",
"        </p>",
"        <p>",
"         DIF: staining of transitional epithelium (rodent bladder)",
"        </p>",
"       </td>",
"       <td>",
"        IIF: antibodies to multiple antigens (desmoplakins, desmogleins, bullous pemphigoid antigen 1, etc.)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HSV: herpes simplex virus; H&amp;E: hematoxylin and eosin pathology stain; DIF: direct immunofluorescence; IIF: indirect immunofluorescence; PCR: polymerase chain reaction.",
"     <br>",
"      * Skin predominant forms of bullous pemphigoid, linear IgA bullous dermatosis, and epidermolysis bullosa acquisita may also present with mucosal involvement.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
