var title_f6_37_6748="Stages of pediatric heart failure and therapy";
var content_f6_37_6748=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F60553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F60553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Stages of heart failure in infants and children and recommended therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup span=\"3\" width=\"30%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Example",
"       </td>",
"       <td class=\"subtitle1\">",
"        Therapy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        Patients with increased risk of developing HF, but with normal cardiac function and chamber size",
"       </td>",
"       <td>",
"        Exposure to cardiotoxic agents, family history of heritable cardiomyopathy, univentricular heart, congenitally corrected transposition of the great arteries",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        B",
"       </td>",
"       <td>",
"        Patients with abnormal cardiac morphology or function, with no symptoms of HF, past or present",
"       </td>",
"       <td>",
"        Aortic insufficiency with LV enlargement, history of anthracycline exposure with decreased LV systolic function",
"       </td>",
"       <td>",
"        ACE inhibitors for patients with systemic ventricular dysfunction",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        Patients with structural or functional heart disease, and past or current symptoms of HF",
"       </td>",
"       <td>",
"        Symptomatic cardiomyopathy or congenital heart defect with ventricular pump dysfunction",
"       </td>",
"       <td>",
"        ACE inhibitors, aldosterone antagonists and beta blockers for remodeling reversal; low dose digoxin and diuretics for symptom control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D",
"       </td>",
"       <td>",
"        Patients with end-stage HF requiring specialized interventions",
"       </td>",
"       <td>",
"        Marked symptoms at rest despite maximal medical therapy",
"       </td>",
"       <td>",
"        Intravenous diuretics and/or inotropes, positive pressure ventilation, mechanical circulatory support, heart transplantation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HF: heart failure; LV: left ventricular; ACE inhibitors: angiotensin converting enzyme inhibitors.",
"    </div>",
"    <div class=\"reference\">",
"     From: Rosenthal D, Chrisant MR, Edens E, et al. International Society for Heart and Lung Transplantation: Practice guidelines for management of heart failure in children. J Heart Lung Transplant 2004; 23:1313. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
