var title_f6_43_6844="Pegasys Copegus dose mod";
var content_f6_43_6844=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F75290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F75290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    PEGASYS (peginterferon)/COPEGUS (ribavirin) dosage modification guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Laboratory values",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Discontinue if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        PEGASYS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ANC &ge;750/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Maintain 180 mcg",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         ANC &lt;500/mm",
"         <sup>",
"          3",
"         </sup>",
"         , treatment should be suspended until ANC values return to more than 1000/mm",
"         <sup>",
"          3",
"         </sup>",
"        </p>",
"        <p>",
"         Reinstitute at 90&nbsp;mcg and monitor ANC",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ANC &lt;750/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Reduce to 135 mcg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Platelet &ge;50,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Maintain 180 mcg",
"       </td>",
"       <td rowspan=\"2\">",
"        Platelet count &lt;25,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Platelet &lt;50,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Reduce to 90 mcg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        COPEGUS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemoglobin in patients with no cardiac disease",
"       </td>",
"       <td>",
"        Reduce to 600 mg/day* if &lt;10 g/dL",
"       </td>",
"       <td>",
"        &lt;8.5 g/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemoglobin in patients with history of stable cardiac disease",
"       </td>",
"       <td>",
"        Reduce to 600 mg/day* if &ge;2 g/dL decrease in hemoglobin during any 4-week period treatment",
"       </td>",
"       <td>",
"        &lt;12 g/dL despite 4 weeks at reduced dose",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * One 200 mg tablet in the morning and two 200 mg tablets in the evening.",
"    </div>",
"    <div class=\"reference\">",
"     Table from: Pegasys (peginterferon alfa-2a) Prescribing Information and Medication Guide. Roche Pharmaceuticals, Nutley, NJ. Available online at: file://www.rocheusa.com/products/pegasys/pi.pdf (Accessed on September 4, 2007).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
