" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Physiology of corticotropin-releasing hormone",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/44/6857/contributors\">",
"     Author",
"    </a>",
