" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Regulation of myelopoiesis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/44/6858/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?6/44/6858/contributors\">",
"     Colin A Sieff, MB, BCh, FRCPath",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
