var title_f6_44_6860="Initiation ADHD drugs";
var content_f6_44_6860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F66213&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F66213&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for initiation of pharmacotherapy in children with ADHD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Diagnostic assessment is complete and confirms diagnosis of ADHD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child is age six years or older*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Parents accept medication as a contribution to management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        School will cooperate in administration and monitoring",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No previous sensitivity to the chosen medication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child has normal heart rate and blood pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child is seizure free",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child does not have Tourette syndrome",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child does not have pervasive developmental delay",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Child does not have significant anxiety",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Substance abuse among household members is not a concern (for children who will be treated with immediate release stimulants)",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ADHD: attention deficit hyperactivity disorder.",
"     <br/>",
"     * Children younger than six years should be managed by or in consultation with a specialist since the effects of stimulants on preschool children are unpredictable.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     It is not safe to permit the child or adolescent to take his or her own medication to school.",
"     <br/>",
"     &Delta; Children with these conditions should be managed by, or in consultation with a specialist.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     Atomoxetine, osmotic release methylphenidate, or the methylphenidate patch are alternatives to immediate-release stimulant preparations for children who live in households where substance abuse is a concern.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Hill T, Taylor E. An auditable protocol for treating attention deficit/hyperactivity disorder. Arch Dis Child 2001; 84:404.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
