var title_f6_45_6878="Causes of dyspnea in palliative care and potential treatments";
var content_f6_45_6878=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PALC%2F83934&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PALC%2F83934&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of dyspnea in palliative care and potential treatments",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disease process",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential interventions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Airway obstruction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Airway obstruction by tumor or by lymphadenopathy",
"       </td>",
"       <td>",
"        <p>",
"         Airway laser/cautery*, stent*, radiation therapy (RT)*, resection*, glucocorticoids",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bronchoconstriction (COPD, asthma)",
"       </td>",
"       <td>",
"        Bronchodilators, glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Retained or excess secretions",
"       </td>",
"       <td>",
"        Anticholinergic agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vocal cord paralysis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Obstructive sleep apnea",
"       </td>",
"       <td>",
"        Continuous positive airway pressure (CPAP) at night",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Pulmonary parenchymal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Widespread tumor metastases",
"       </td>",
"       <td>",
"        RT*, chemotherapy*, glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumonia, aspiration",
"       </td>",
"       <td>",
"        Antibiotics, pulmonary toilet, aspiration precautions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Heart failure",
"       </td>",
"       <td>",
"        Diuretics, ACE inhibitors, etc",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pericardial disease, tamponade",
"       </td>",
"       <td>",
"        Pericardiocentesis*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lymphangitic tumor",
"       </td>",
"       <td>",
"        Diuretics, glucocorticoids, chemotherapy*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lung resection (eg, lobectomy, pneumonectomy)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumonitis caused by antineoplastic therapy",
"       </td>",
"       <td>",
"        Glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Radiation pneumonitis and fibrosis",
"       </td>",
"       <td>",
"        Glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Interstitial lung disease",
"       </td>",
"       <td>",
"        Glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Pulmonary vascular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Venous thromboembolism",
"       </td>",
"       <td>",
"        Anticoagulation*, thrombolysis*, inferior vena cava filter*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sinusoidal obstruction syndrome (veno-occlusive disease)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tumor thromboembolism",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulmonary hypertension",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Superior vena cava syndrome",
"       </td>",
"       <td>",
"        Glucocorticoids, RT*, chemotherapy*, stent*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Pleural",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pleural effusion (eg, malignant, drug-induced)",
"       </td>",
"       <td>",
"        Indwelling pleural catheter with intermittent drainage; pleurodesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pleural tumor",
"       </td>",
"       <td>",
"        RT*, chemotherapy*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumothorax",
"       </td>",
"       <td>",
"        Chest tube",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pleural effusion due to lung entrapment by tumor",
"       </td>",
"       <td>",
"        Indwelling pleural catheter with intermittent drainage as guided by symptoms*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Inspiratory muscle weakness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cachexia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Electrolyte imbalance",
"       </td>",
"       <td>",
"        Adjust electrolytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neuromuscular disease, including paraneoplastic syndromes",
"       </td>",
"       <td>",
"        Noninvasive positive pressure ventilation (NPPV)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Steroid myopathy",
"       </td>",
"       <td>",
"        Physical therapy, reduce dose of glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Diaphragmatic paralysis, phrenic nerve paralysis",
"       </td>",
"       <td>",
"        NPPV*, diaphragmatic pacing*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Decreased chest wall compliance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Restriction post thoracotomy",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Restriction due to advanced chest wall tumor (eg, inflammatory breast cancer)",
"       </td>",
"       <td>",
"        RT*, chemotherapy*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Massive ascites",
"       </td>",
"       <td>",
"        Drainage*, including indwelling catheter with intermittent drainage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Massive abdominal organomegaly",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Obesity",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Systemic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anemia",
"       </td>",
"       <td>",
"        Red blood cell transfusion*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acidosis",
"       </td>",
"       <td>",
"        Correct metabolic abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arrhythmias",
"       </td>",
"       <td>",
"        Usual treatments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Neuropsychiatric",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pain",
"       </td>",
"       <td>",
"        Opioids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Depression",
"       </td>",
"       <td>",
"        Antidepressants, cognitive behavioral therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anxiety, including hyperventilation",
"       </td>",
"       <td>",
"        Anxiolytic medication, cognitive behavioral therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Respiratory panic",
"       </td>",
"       <td>",
"        Anxiolytics, antidepressants, cognitive behavioral therapy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     &nbsp;",
"    </p>",
"    <div class=\"footnotes\">",
"     RT: radiation therapy; COPD: chronic obstructive pulmonary disease; ACE: angiotensin converting enzyme; NPPV: noninvasive positive pressure ventilation.",
"     <br>",
"      * Decisions regarding all therapies and disease-modifying treatments must be made in light of the expected burdens and benefits and be consistent with the patient's long-term and short-term goals of care.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
