" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Nutritional assessment in chronic liver disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/46/6889/contributors\">",
