var content_f6_47_6908=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57206&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57206&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Terminology used to describe intellectual disability in different countries",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Country and/or language",
"       </td>",
"       <td class=\"subtitle1\">",
"        Term",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        United States",
"       </td>",
"       <td>",
"        Intellectual disability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Australia",
"       </td>",
"       <td>",
"        Intellectual disability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Canada (English, French)",
"       </td>",
"       <td>",
"        Mental deficiency, intellectual handicap",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        England",
"       </td>",
"       <td>",
"        Learning disability*, intellectual disability, developmental disability",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        France",
"       </td>",
"       <td>",
"        Mental deficiency, mental apraxia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Germany",
"       </td>",
"       <td>",
"        Mental handicap, mental retardation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Italy",
"       </td>",
"       <td>",
"        Mental delay, mentally deficient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Estonia",
"       </td>",
"       <td>",
"        Mental retardation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Puerto Rico",
"       </td>",
"       <td>",
"        Mentally slowed down",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spain",
"       </td>",
"       <td>",
"        Mental delay",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In the United States, the term \"learning disability\" usually denotes a specific learning disability (eg, dyslexia) rather than intellectual disability.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      In the United States, the term \"developmental disability\" does not necessarily involve intellectual disability. For example, a developmental disability could be limited to motor dysfunction or developmental-behavioral dysfunction, in the absence of intellectual disability.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Schroeder SR, Gerry M, Gertz G, Velazquez F. Usage of the Term \"Mental Retardation:\" Language, Image and Public Education. Kansas University Center on Developmental Disabilities; Center for the Study of Family, Neighborhood and Community Policy, The University of Kansas. 2002. p.86.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
