var title_f6_5_6235="Criteria for essential tremor";
var content_f6_5_6235=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F62078&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F62078&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for diagnosis of essential tremor",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Core criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Secondary criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilateral action tremor of the hands and forearms (but not rest tremor)",
"       </td>",
"       <td>",
"        Long duration (&gt;3 years)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absence of other neurologic signs, with the exception of cogwheel phenomenon",
"       </td>",
"       <td>",
"        Positive family history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May have isolated head tremor with no signs of dystonia",
"       </td>",
"       <td>",
"        Beneficial response to alcohol",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permisssion from: Bain P, Brin M, Deuschl G, et al. Criteria for the diagnosis of essential tremor. Neurology 2000; 54(suppl 4):s7. Copyright &copy; 2000 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
