var title_f6_51_6972="Empiric treatment psoas abscess";
var content_f6_51_6972=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64723&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64723&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric antiobitic therapy for psoas abscess in adults*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Empiric therapy with activity against methicillin-resistant S. aureus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Preferred",
"      </td>",
"      <td>",
"       Vancomycin (30 mg/kg IV every 24 h in 2 equally divided doses)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Alternatives",
"      </td>",
"      <td>",
"       Daptomycin (6 mg/kg IV once daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       Linezolid (600 mg IV or orally twice daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\" colspan=\"2\">",
"       PLUS",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Empiric therapy with activity against gram-negative and anaerobic pathogens",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Monotherapy with a beta-lactam/beta-lactamase inhibitor, such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Ampicillin-sulbactam (3 g every 6 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Piperacillin/tazobactam (3.375 g every 6 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Ticarcillin-clavulanate (3.1 g every 4 to 6 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       A third generation cephalosporin such as ceftriaxone (1 g IV every 24 hours)",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole (500 mg IV every 8 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       A fluoroquinolone (eg, ciprofloxacin 400 mg IV every 12 hours or levofloxacin 500 mg IV daily)",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole (500 mg IV every 8 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Monotherapy with a carbapenem&bull; such as one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Imipenem (500 mg every 6 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Meropenem (1 g every 8 hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\" colspan=\"2\">",
"       Ertapenem (1 g daily)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Antibiotic therapy should be tailored to culture and sensitivity data when available.",
"     <br>",
"      &bull; Carbapenems should not be used for patients with history of immediate-type hypersensitivity to beta-lactams.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
