var title_f6_51_6973="Physical findings in obese children";
var content_f6_51_6973=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F73272&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F73272&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Findings of note on physical examination in children with obesity",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Exam finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical concern raised",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Short stature",
"        </p>",
"        OR unexplained decrease in height velocity",
"       </td>",
"       <td>",
"        <p>",
"         Height &lt;50th percentile with weight &gt;95th percentile* (if not explained by familial short stature)",
"        </p>",
"        Growth velocity &lt;5 cm/year in a prepubertal child, or declining across over two or more height percentile curves on a standard chart (eg, decreasing from the 90th to the 50th percentile)",
"       </td>",
"       <td>",
"        Endocrine or genetic condition (eg, Cushing syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypertension",
"       </td>",
"       <td>",
"        Hypertension if systolic or diastolic blood pressure &gt;95th percentile for age, gender, and height on &ge;3 occasions",
"       </td>",
"       <td>",
"        Essential hypertension, renal disease, or Cushing syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acanthosis nigricans",
"       </td>",
"       <td>",
"        Hyperpigmented, thickened, velvety skin in body folds and creases, particularly neck",
"       </td>",
"       <td>",
"        Increased risk of insulin resistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Excessive acne, hirsutism",
"       </td>",
"       <td>",
"        Hirsutism: excessive growth of hair in atypical areas, such as face and neck",
"       </td>",
"       <td>",
"        Polycystic ovary syndrome (PCOS)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Violaceous striae",
"       </td>",
"       <td>",
"        Linear lesions, red, pink, or purple in color, particularly on abdomen",
"       </td>",
"       <td>",
"        Cushing syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Papilledema, cranial nerve VI paralysis",
"       </td>",
"       <td>",
"        Optic disc swelling on funduscopic exam, caused by increased intracranial pressure",
"       </td>",
"       <td>",
"        Pseudotumor cerebri (idiopathic intracranial hypertension)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tonsillar hypertrophy",
"       </td>",
"       <td>",
"        Tonsils occupy more than 50 percent of the lateral dimension of oropharynx",
"       </td>",
"       <td>",
"        Obstructive sleep apnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Goiter",
"       </td>",
"       <td>",
"        Enlarged or swollen thyroid gland",
"       </td>",
"       <td>",
"        Hypothyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wheezing",
"       </td>",
"       <td>",
"        High-pitched whistling on auscultation",
"       </td>",
"       <td>",
"        Asthma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatomegaly, right upper quadrant tenderness",
"       </td>",
"       <td>",
"        Increased liver span",
"       </td>",
"       <td>",
"        Non-alcoholic fatty liver disease, or gallstones",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Micropenis",
"       </td>",
"       <td>",
"        Unusually small penis",
"       </td>",
"       <td>",
"        In most cases the small-appearing penis is actually normal size; the length is buried under suprapubic fat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Undescended testes",
"       </td>",
"       <td>",
"        Testicle not palpable in scrotum",
"       </td>",
"       <td>",
"        Prader-Willi syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal gait, limp, pain in hip or groin, limited range of motion in hip",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Slipped capital femoral epiphysis (SCFE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bowing of tibia",
"       </td>",
"       <td>",
"        Lower leg angles inward, causing a bowleg appearance",
"       </td>",
"       <td>",
"        Blount disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Small hands and feet, or polydactyly",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Genetic condition (eg, Prader-Willi syndrome or Bardet-Biedl syndrome)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Most children with obesity who have not completed linear growth are relatively tall for their age. Therefore, height &lt;50th percentile is unusual.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
