" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Ocular myasthenia gravis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/53/7001/contributors\">",
