var title_f6_58_7084="Stepwise Rx plantar fasciitis";
var content_f6_58_7084=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F55469&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F55469&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Step-wise therapy for plantar fasciitis in the young athlete",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        First-line therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NSAIDs or steroid burst",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Icing (massage)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stretching heel cords and gastrocnemius-soleus muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Proper shoes for daily and sports activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No barefoot walking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low-dye taping or hallux strapping",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Over-the-counter orthosis or temporary heelcups,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Felt and silicone inserts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Relative rest*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Strengthening of the intrinsic muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Second-line therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Physical therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iontophoresis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ultrasonography with deep tissue cross friction massage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider orthotics although taping is usually better",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thomas heel (puts the calcaneus into varus and unloads the plantar fascia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Night splints (for patients with persistent first-step symptoms)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cortisone injection (with ultrasound guidance if necessary)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short leg walking cast for 2-4 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider steel shank rocker bottom shoes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Third-line therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extracorporeal shock wave application",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Relative rest: avoidance of activities that cause pain.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
