var title_f6_59_7101="Food-borne diseases V";
var content_f6_59_7101=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F55831&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F55831&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for confirmation of food-borne disease outbreaks-V",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiologic agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incubation period",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Chemical (cont.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"4\">",
"        Marine toxins (cont.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\" rowspan=\"3\">",
"        Scombroid toxin (histamine)",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        1 min-3 hrs, usually &lt;1 hr",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        Flushing, dizziness, burning of mouth and throat, headache, gastrointestinal symptoms, urticaria, and generalized pruritus",
"       </td>",
"       <td class=\"sublist_other\">",
"        Demonstration of histamine in epidemiologically implicated food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Clinical syndrome among persons who have eaten type of fish previously associated with histamine fish poisoning (eg, mahi-mahi or fish of order Scomboidei)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\" rowspan=\"3\">",
"        Paralytic or neurotoxic shellfish poison",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        30 min-3 hrs",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        Paresthesia of lips, mouth or face, and extremities; intestinal symptoms or weakness, including respiratory difficulty",
"       </td>",
"       <td class=\"sublist_other\">",
"        Detection of toxin in epidemiologically implicated food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Detection of large numbers of shellfish-poisoning-associated species of dinoflagellates in water from which epidemiologically implicated mollusks are gathered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\" rowspan=\"3\">",
"        Puffer fish, tetrodotoxin",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        10 min-3 hrs, usually 10-45 mins",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        Paresthesia of lips, tongue, face, or extremities, often following numbness; loss of proprioception or \"floating\" sensations",
"       </td>",
"       <td class=\"sublist_other\">",
"        Demonstration of tetrodotoxin in epidemiologically implicated fish",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Clinical syndrome among persons who have eaten puffer fish",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Heavy metals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Antimony",
"       </td>",
"       <td rowspan=\"6\">",
"        5 min-8 hrs, usually &lt;1 hr",
"       </td>",
"       <td rowspan=\"6\">",
"        Vomiting, often metallic taste",
"       </td>",
"       <td rowspan=\"6\">",
"        Demonstration of high concentration of metal in epidemiologically implicated food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Cadmium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Copper",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Iron",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Tin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Zinc",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Monosodium glutamate (MSG)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        3 mins-2 hrs, usually &lt;1 hr",
"       </td>",
"       <td>",
"        Burning sensation in chest, neck, abdomen, or extremities; sensation of lightness and pressure over face or heavy feeling in chest",
"       </td>",
"       <td>",
"        Clinical syndrome among persons who have eaten food containing MSG (ie, usually &ge;1.5 g MSG)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: file://www.cdc.gov/mmwr//mmwr.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
