" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Ambulatory blood pressure monitoring and white coat hypertension in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/60/7113/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?6/60/7113/contributors\">",
"     Norman M Kaplan, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?6/60/7113/contributors\">",
"     Section Editors",
"    </a>",
