var title_f6_60_7116="Adv prostate ca treat";
var content_f6_60_7116=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F60321&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F60321&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The advantages of the main treatment options for early prostate cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       External beam radiation therapy (EBRT)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cancer can be controlled long-term",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Very low risk of urinary incontinence (leaking urine)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Can be used to cure men of nearly any age, even men who have underlying medical problems",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Good chance for a cure in men with high-risk prostate cancer when combined with androgen deprivation therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Treatments can get rid of cancer cells that have grown through the covering of the prostate (called the prostate capsule)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Brachytherapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chance of controlling cancer similar to surgery (prostatectomy) and EBRT for men whose cancer has not spread outside the prostate",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Quicker than EBRT (single treatment)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Risk of leaking urine is low as long as the man has not had previous prostate surgery",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Can be used to cure men of nearly any age, even in men who have certain underlying medical problems",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Radical prostatectomy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cancer can be controlled long-term",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Can predict chance of cure and risk that cancer will come back more precisely than other types of treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Nearby lymph nodes can be examined and removed (if needed) during surgery",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Active surveillance (\"watch and wait\")",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reduces the chance that you will get unnecessary treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoids or delays complications that can happen during treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Has no effect on work or social activities",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Vogelzang NJ, Scardino PT, Shipley WU, et al. Comprehensive textbook of genitourinary oncology, 3rd Edition. Lippincott Williams &amp; Wilkins 2005. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
