var title_f6_60_7117="Risk of death or MI in UA";
var content_f6_60_7117=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F55745&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F55745&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guidelines: Short-term risk of death or nonfatal myocardial infarction in patients with symptoms suggesting unstable angina",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        High risk",
"       </td>",
"       <td class=\"subtitle1\">",
"        Intermediate risk",
"       </td>",
"       <td class=\"subtitle1\">",
"        Low risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At least one of the following",
"features must be present",
"       </td>",
"       <td>",
"        No high-risk feature but must",
"have any of the following",
"       </td>",
"       <td>",
"        No high- or intermediate-risk",
"feature but may have any of the following",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        History",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Accelerating tempo of ischemic symptoms in preceding 48",
"hours",
"       </td>",
"       <td>",
"        Prior MI, peripheral or cerebrovascular disease, CABG,",
"or prior aspirin use",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Character",
"of pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"3\">",
"        Prolonged (&gt;20 min) and ongoing rest pain",
"       </td>",
"       <td>",
"        Prolonged rest angina (&gt;20 min), now resolved,",
"with moderate or high likelihood of CAD",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Angina provoked at a low threshold, increased angina frequency, severity or duration. New onset angina with onset two weeks to two months prior to presentation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rest angina (&gt;20 min) or relieved with rest or",
"sublingual NTG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nocturnal angina",
"       </td>",
"       <td>",
"        New or progressive class III or IV angina in past two weeks without prolonged (&gt;20 min) rest pain but with intermediate or high likelihood of CAD.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Clinical",
"findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulmonary edema, most likely due to ischemia",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Age &gt;70 years",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New or worsening mitral regurgitation murmur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        S3 or new or worsening rales",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypotension, bradycardia, tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Age &gt;75 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        ECG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Angina at rest with transient ST segment changes",
"&gt;0.5 mV",
"       </td>",
"       <td>",
"        T-wave changes",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Normal or unchanged ECG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New or presumed new bundle branch block",
"       </td>",
"       <td>",
"        Pathologic Q waves or resting ST depression less than 1 mm in multiple lead groups",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sustained ventricular tachycardia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Cardiac",
"markers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Markedly elevated (eg, cTnT or cTnI &gt;0.1 ng/mL)",
"       </td>",
"       <td>",
"        Slightly elevated (eg, cTnT &gt;0.01 but",
"&lt;0.1 ng/mL)",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Anderson J, Adams C, Antman E, et al. ACC/AHA 2007 guidelines for the management of patients with unstable angina/non-ST-elevation myocardial infarction: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to revise the 2002 Guidelines for the Management of Patients with Unstable Angina/Non-ST-Elevation Myocardial Infarction): developed in collaboration with the American College of Emergency Physicians, American College of Physicians, Society for Academic Emergency Medicine, Society for Cardiovascular Angiography and Interventions, and Society of Thoracic Surgeons. J Am Coll Cardiol 2007; 50:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
