" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of nocturnal enuresis in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/0/7178/contributors\">",
