var title_f7_0_7179="Cullens definition IEI";
var content_f7_0_7179=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F60215&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F60215&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cullen's definition of idiopathic environmental intolerance",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        1. A disorder acquired after a documented environmental exposure, insult, or illness.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Symptoms involving more than one organ system.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Symptoms that recur and abate in response to predictable stimuli.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Symptoms elicited by exposures to chemical of diverse structural class and toxicologic modes of action.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Symptoms that are elicited by exposures that are demonstrable.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6. Exposures that elicit symptoms are generally lower than 1 percent of established threshold limit values.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7. No single widely available test of organ system function can explain symptoms.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
