var title_f7_11_7355="Causes malignancy ascites";
var content_f7_11_7355=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F65840&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F65840&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of malignancy-related ascites",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Cause",
"      </td>",
"      <td class=\"subtitle1\">",
"       Frequency among patients with malignancy-related ascites",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Peritoneal carcinomatosis",
"      </td>",
"      <td>",
"       53 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Massive liver metastases causing portal hypertension",
"      </td>",
"      <td>",
"       13 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Peritoneal carcinomatosis plus massive liver metastases",
"      </td>",
"      <td>",
"       13 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hepatocellular carcinoma plus cirrhosis",
"      </td>",
"      <td>",
"       13 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chylous ascites due to malignancy, usually lymphoma",
"      </td>",
"      <td>",
"       7 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Budd-Chiari syndrome due to malignancy occluding the hepatic veins",
"      </td>",
"      <td>",
"       Rare",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Runyon, BA, Hoefs, JC, Morgan, TR. Ascitic fluid analysis in malignancy-related ascites. Hepatology 1998; 8:1104.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
