var title_f7_11_7356="Food-borne diseases VII";
var content_f7_11_7356=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F77344&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F77344&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for confirmation of food-borne disease outbreaks-VII",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiologic agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incubation period",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Viral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis A",
"       </td>",
"       <td>",
"        15-50 days, median: 28 days",
"       </td>",
"       <td>",
"        Jaundice, dark urine, fatigue, anorexia, nausea",
"       </td>",
"       <td>",
"        Detection of IgM anti-hepatitis A virus in serum from two or more persons who consumed epidemiologically implicated food",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Norwalk family of viruses, small round-structured viruses (SRSV)",
"       </td>",
"       <td rowspan=\"3\">",
"        15-77 hrs, usually 24-48 hrs",
"       </td>",
"       <td rowspan=\"3\">",
"        Vomiting, cramps, diarrhea, headache",
"       </td>",
"       <td>",
"        More than fourfold rise in antibody titer to Norwalk virus or Norwalk-like virus in acute and convalescent sera in most serum pairs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visualization of small, round-structured viruses that react with patient's convalescent sera but not acute sera - by immune electron microscopy. Assays based on molecular diagnostic (eg, polymerase chain reaction [PCR], probes, or assays for antigen and antibodies from expressed antigen) are available in reference laboratories.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"indent1\">",
"        Astrovirus, calicivirus, others",
"       </td>",
"       <td>",
"        15-77 hrs, usually 24-48 hrs",
"       </td>",
"       <td>",
"        Vomiting, cramps, diarrhea, headache",
"       </td>",
"       <td>",
"        Visualization of small, round-structured viruses that react with patient's convalescent sera but not acute sera - by immune electron microscopy. Assays based on molecular diagnostics (eg, PCR, probes, or assays for antigen and antibodies from expressed antigen) are available in reference laboratories.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: file://www.cdc.gov/mmwr//mmwr.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
