var script_f7_16_7425=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function RansonCriteria_fx() {",
"with(document.RansonCriteria_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4.checked){",
"Score = Score + 1;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"if (cc8.checked){",
"Score = Score + 1;",
"}",
"if (cc9.checked){",
"Score = Score + 1;",
"}",
"if (cc10.checked){",
"Score = Score + 1;",
"}",
"if (cc11.checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 2)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 5)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 6) && (Score <= 11)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
