var title_f7_16_7435="Eval cancer treatment induced diarrhea";
var content_f7_16_7435=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F52476&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F52476&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of cancer treatment induced diarrhea",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Obtain history of onset and duration of diarrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Describe number of stools&nbsp;and stool composition (eg, watery, blood in stool, nocturnal) and compare to baseline stool frequency/consistency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assess patient for fever, dizziness, abdominal pain/cramping, or weakness (ie, rule out risk for sepsis, bowel obstruction, dehydration)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medications profile (ie, to identify diarrhea-inducing agents)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dietary profile (ie, to identify diarrhea-enhancing foods)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Benson AB, Ajani JA, Catalano RB, et al. Recommended guidelines for the treatment of cancer treatment-induced diarrhea. J Clin Onc 2004; 22:2918. Copyright &copy; 2004 American Society of Clinical Oncology.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
