var title_f7_16_7436="Causes of ptosis";
var content_f7_16_7436=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F63515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F63515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cardinal features of common underlying causes of ptosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cause",
"       </td>",
"       <td class=\"subtitle1\">",
"        Levator function",
"       </td>",
"       <td class=\"subtitle1\">",
"        Eyelid crease margin",
"       </td>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Other clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital abnormality of the levator muscle*",
"       </td>",
"       <td>",
"        Reduced",
"       </td>",
"       <td>",
"        Crease often absent",
"       </td>",
"       <td>",
"        Often unilateral",
"       </td>",
"       <td>",
"        Many patients also have amblyopia, strabismus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aponeurotic ptosis",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Often increased",
"       </td>",
"       <td>",
"        Uni- or bilateral",
"       </td>",
"       <td>",
"        Isolated finding of ptosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cranial nerve 3 palsy",
"       </td>",
"       <td>",
"        Reduced",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Usually unilateral",
"       </td>",
"       <td>",
"        Impaired extraocular movement in ipsilateral eye. If ipsilateral pupil dilated, urgent evaluation for aneurysm is required.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Horner's syndrome",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Usually unilateral",
"       </td>",
"       <td>",
"        Ipsilateral miotic pupil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myasthenia",
"       </td>",
"       <td>",
"        Reduced",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Uni- or bilateral",
"       </td>",
"       <td>",
"        Variable and fatigable. Diplopia and extraocular movement abnormalities often present.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Muscle disease",
"       </td>",
"       <td>",
"        Reduced",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Usually bilateral",
"       </td>",
"       <td>",
"        Orbicularis oculi, other extraocular or bulbar muscles may be affected",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Other, less common causes of congenital ptosis are discussed in text.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
