var title_f7_18_7468="AHA lesion classification";
var content_f7_18_7468=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F56922&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F56922&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for American Heart Association lesion classification system and correspondence with classification of gross arterial specimens",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        AHA grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments and corresponding gross",
"classification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        Normal artery with or without adaptive intimal thickening; no",
"hold",
"       </td>",
"       <td>",
"        Normal tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Isolated MFCs containing lipid; no extracellular lipid;",
"variable adaptive intimal thickening grossly with lipid staining",
"       </td>",
"       <td>",
"        Initial atherosclerotic lesion, sometimes visible grossly",
"with lipid staining",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Numerous MFCs, often in layers, with fine particles of",
"extracellular licid; no distinct pools of extracellular lipid: variable",
"adaptive intimal thickening",
"       </td>",
"       <td>",
"        Fatty streak, visible grossly with III staining",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Numerous MFCs with &ge;",
"pools of extracellular lipid; no well-defined core of extracellular",
"lipid",
"       </td>",
"       <td>",
"        Fatty plaque, raised fatty streak, intermediate lesion, or",
"transitional lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Numerous MFGs plus well-defined core of extracellular lipid,",
"but with luminal surface covered by relatively normal intima",
"       </td>",
"       <td>",
"        Atheroma, fibrous plaque, or raised lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        Numerous MFCs, well-defined core or multiple cores of",
"extracellular lipid, plus reactive fibrotic cap, vascularization, or",
"calcium",
"       </td>",
"       <td>",
"        Fibroartherema, fibrous plaque, or raised lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6",
"       </td>",
"       <td>",
"        All of the above plus surface defect, hematoma, hemorrhage,",
"or thrombosis",
"       </td>",
"       <td>",
"        Complicated lesion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MFC: macrophage foam cell; AHA: American Heart Association.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
