var title_f7_19_7484="Classification of osteochondritis dissecans";
var content_f7_19_7484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F85755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F85755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of osteochondritis dissecans",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"5%\">",
"     </colgroup>",
"     <colgroup span=\"3\" width=\"31%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Plain radiograph findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Magnetic resonance imaging findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Arthroscopic findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        I",
"       </td>",
"       <td>",
"        Depressed osteochondral fragment",
"       </td>",
"       <td>",
"        Articular cartilage thickening and low signal changes",
"       </td>",
"       <td>",
"        Irregular and softened articular cartilage with no fragment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        II",
"       </td>",
"       <td>",
"        Osteochondral fragment attached by an osseous bridge",
"       </td>",
"       <td>",
"        Articular cartilage breached, low signal rim behind fragment representing fibrous attachment",
"       </td>",
"       <td>",
"        Articular cartilage breached with definable fragment that is not displaceable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        III",
"       </td>",
"       <td>",
"        Detached, nondisplaced fragment",
"       </td>",
"       <td>",
"        Articular cartilage breached, high signal changes behind fragment indicating synovial fluid between fragment and subchondral bone",
"       </td>",
"       <td>",
"        Articular cartilage breached with definable fragment that is displaceable but attached by overlying articular cartilage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        IV",
"       </td>",
"       <td>",
"        Displaced fragment",
"       </td>",
"       <td>",
"        Loose foreign body",
"       </td>",
"       <td>",
"        Loose foreign body",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        DeLee JC, Drez Jr D, Miller MD. DeLee &amp; Drez's Orthopaedic Sports Medicine Principles and Practice, 2nd, Elsevier Science, Philadelphia 2003.",
"       </li>",
"       <li>",
"        Dipaola JD, Nelson DW, Colville MR. Characterizing osteochondral lesions by magnetic resonance imaging. Arthroscopy 1991; 7:101.",
"       </li>",
"       <li>",
"        Guhl JF. Arthroscopic treatment of osteochondritis dissecans. Clin Orthop Relat Res 1982; :65.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
