"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H6801610\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY &amp; RECOMMENDATIONS",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
