" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology and molecular mechanisms of drug-resistant tuberculosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/20/7497/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/20/7497/contributors\">",
"     Neil W Schluger, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/20/7497/contributors\">",
