" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of hypothermia in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/22/7530/contributors\">",
