var title_f7_22_7531="Preventing fatal asthma";
var content_f7_22_7531=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F58651&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F58651&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Steps to prevent fatal and nonfatal asthma exacerbations",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Provide verbal and written education about trigger avoidance (eg, aeroallergens, aspirin, NSAIDs)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Educate about early warning signs of deterioration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Use objective measures of airflow limitation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Review prednisone based action plan at each visit",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Arrange frequent visits to assess asthma control",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Optimize controller therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Encourage adherence to medications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Encourage smoking cessation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Address illicit drug use",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Provide annual influenza vaccination",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
