" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of low-risk endometrial cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/23/7542/contributors\">",
