" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Anaphylaxis in pregnant and breastfeeding women",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/27/7610/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/27/7610/contributors\">",
"     Michael Schatz, MD, MS",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/27/7610/contributors\">",
