var title_f7_27_7613="Contraindications to cardiac transplantation";
var content_f7_27_7613=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F83788&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F83788&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contraindications to cardiac transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Absolute contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Systemic illness with a life expectancy &lt;two years despite HT, including:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Active or recent solid organ or blood malignancy within five years (eg, leukemia, low-grade neoplasms of prostate with persistently elevated prostate-specific antigen)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        AIDS with frequent opportunistic infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Systemic lupus erythematosus, sarcoid, or amyloidosis that has multisystem involvement and is still active",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Irreversible renal or hepatic dysfunction in patients considered for only HT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Significant obstructive pulmonary disease (FEV",
"        <sub>",
"         1",
"        </sub>",
"        &lt;1 L/min)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fixed pulmonary hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pulmonary artery systolic pressure &gt;60 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Mean transpulmonary gradient &gt;15 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pulmonary vascular resistance &gt;6 Wood units",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Relative contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age &gt;72 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any active infection (with exception of device-related infection in VAD recipients)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Active peptic ulcer disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe diabetes mellitus with end-organ damage (neuropathy, nephropathy, or retinopathy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Severe peripheral vascular or cerebrovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Peripheral vascular disease not amenable to surgical or percutaneous therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Symptomatic carotid stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ankle brachial index &lt;0.7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Uncorrected abdominal aortic aneurysm &gt;6 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Morbid obesity (body mass index &gt;35 kg/m",
"        <sup>",
"         2",
"        </sup>",
"        ) or cachexia (body mass index &lt;18 kg/m",
"        <sup>",
"         2",
"        </sup>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Creatinine &gt;2.5 mg/dL or creatinine clearance &lt;25 mL/min*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilirubin &gt;2.5 mg/dL, serum transaminases &gt;3X, INR &gt;1.5 off warfarin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe pulmonary dysfunction with FEV",
"        <sub>",
"         1",
"        </sub>",
"        &lt;40 percent normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recent pulmonary infarction within&nbsp;six to eight weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Difficult-to-control hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irreversible neurological or neuromuscular disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Active mental illness or psychosocial instability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug, tobacco, or alcohol abuse within&nbsp;six months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heparin-induced thrombocytopenia within 100 days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     INR: international normalized ratio.",
"     <br/>",
"     * May be suitable for HT if inotropic support and hemodynamic management produce a creatinine 2 mg/dL and creatinine clearance 50 mL per minute. Transplantation may also be advisable as combined heart-kidney transplantation.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Mancini D, Lietz K. Selection of Cardiac Transplantation Candidates in 2010. Circulation 2010; 122:174. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
