" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Interleukin-2 and other immunotherapies for advanced melanoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/28/7625/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?7/28/7625/contributors\">",
"     Jeffrey A Sosman, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?7/28/7625/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
